import axios from "axios";
import React, { useEffect } from "react";
//Bài 112: Bài tập Movie Search App - Đăng ký API
//Bài 113: Bài tập Movie Search App - Fetching dữ liệu
// https://api.themoviedb.org/3/movie/550?api_key=bdfbaf7689830bc3bb7922af5b3c9f42
//https://api.themoviedb.org/3/search/movie?api_key=bdfbaf7689830bc3bb7922af5b3c9f42&query=%27%27
//Bài 114: Bài tập Movie Search App - Styling giao diện
const MoviSearchApp = () => {
  useEffect(() => {
    async function fetchData() {
      const result = await axios.get(
        `https://api.themoviedb.org/3/search/movie?api_key=bdfbaf7689830bc3bb7922af5b3c9f42&query=''`
      );
      console.log(result.data);
    }
    fetchData();
  }, []);
  return (
    <div className="p-10">
      <div className="w-full max-w-[500px] mx-auto mb-20">
        <input
          type="text"
          className="w-full p-5 rounded-lg 
          shadow-[0px_0px_0px_3px_rgba(125,_106,_255,_0.2)]
          border border-purple-500"
          placeholder="Search movie"
          name=""
          id=""
        />
      </div>
      <div className="grid grid-cols-3 gap-10">
        <MovieItem></MovieItem>
        <MovieItem></MovieItem>
        <MovieItem></MovieItem>
      </div>
    </div>
  );
};

const MovieItem = () => {
  return (
    <div className="bg-white p-[10px] shadow-lg">
      <div className="h-[297px]">
        <img
          src="https://images.unsplash.com/photo-1644982648791-a010e61aa845?ixlib=rb-1.2.1&ixid=MnwxMjA3fDF8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=1170&q=80"
          alt=""
          className="w-full h-full rounded-lg object-cover"
        />
      </div>
      <div className="p-[30px]">
        <h3
          className="text-xl mb-4
        text-black font-semibold"
        >
          Hotel Transylvania: Puppy!
        </h3>
        <p className="text-[#999] text-sm">
          The residents of Hotel Transylvania find their world turned
          upside-down when youngster Dennis gets a surprise monster-sized pet.
        </p>
      </div>
    </div>
  );
};
export default MoviSearchApp;
