import React, { createContext, useContext, useState } from "react";
//Bài 190: Thực hành CountContext phần 1
const CountContext = createContext();

const CountProvider = (props) => {
  const [count, setCount] = useState(0);
  const value = [count, setCount];
  return (
    <CountContext.Provider value={value} {...props}></CountContext.Provider>
  );
};

const CountDisplay = () => {
  const [count] = useContext(CountContext);
  return <div>The count is: {count}</div>;
};
const Counter = () => {
  // const setCount = () => {};
  const [, setCount] = useContext(CountContext);
  const increment = () => setCount((count) => count + 1);
  return (
    <button
      onClick={increment}
      className="p-4 bg-purple-400
     text-white
      rounded-md"
    >
      Increment count
    </button>
  );
};
const CountContextApp = () => {
  return (
    <div className="flex items-center justify-center p-5 gap-x-5">
      {/* để sử dụng context thì countDisplay và
       counter phải nằm trong countProvider 
       thì 2 cái kia mới truy xuất được giá trị trong countProvider
       */}
      <CountProvider>
        <CountDisplay></CountDisplay>
        <Counter></Counter>
      </CountProvider>
    </div>
  );
};

export default CountContextApp;
