import React from "react";
import { Formik, Form, Field, ErrorMessage, useField } from "formik";
import * as Yup from "yup";
//Bài 128: Bài tập SignUpForm - Phân tích và code giao diện
//Bài 129: Bài tập SignUpForm - Chia nhỏ và tái sử dụng components
//Bài 130: Bài tập SignUpForm - Tìm hiểu và sử dụng hook useField
const SignUpFormFinal = () => {
  return (
    <Formik
      initialValues={{
        firstName: "",
        lastName: "",
        email: "",
        intro: "",
        job: "",
        terms: false,
      }}
      validationSchema={Yup.object({
        firstName: Yup.string().required("Required"),
        lastName: Yup.string().required("Required"),
        email: Yup.string().email().required("Required"),
        intro: Yup.string().required("Required"),
        job: Yup.string().required("Required"),
        terms: Yup.boolean(),
      })}
      onSubmit={(values) => {
        console.log(values);
      }}
    >
      <Form className="p-10 w-full max-w-[500px] mx-auto" autoComplete="off">
        <MyInput
          label="Firt name"
          name="firstName"
          placeholder="Enter your first name"
          id="firstName"
        ></MyInput>
        <MyInput
          label="Last Name"
          name="lastName"
          placeholder="Enter your last name"
          id="lastName"
        ></MyInput>
        <MyInput
          label="Email address"
          name="email"
          placeholder="Enter your email address"
          id="email"
          type="email"
        ></MyInput>
        <MyTextarea
          label="Introduce yourself"
          name="intro"
          placeholder="Enter your introduce"
          id="intro"
        ></MyTextarea>
        <MySelectbox label="Select your job" name="job" id="job">
          <option value="">--Chon--</option>
          <option value="frontend"> FE developer</option>
          <option value="backend"> BE developer</option>
          <option value="fullstack"> Full developer</option>
        </MySelectbox>
        <MyCheckbox name="terms" id="terms">
          <p className="pt-[-10px]">I accpept the terms and conditians</p>
        </MyCheckbox>
        {/* <div className="flex flex-col gap-2 mb-4">
          <label htmlFor="firstName">FirstName</label>
          <Field
            name="firstName"
            type="text"
            placeholder="Enter your first name..."
            className="p-4 rounded-md border border-gray-400"
          ></Field>
          <div className="text-red-500">
            <ErrorMessage name="firstName"></ErrorMessage>
          </div>
        </div> */}
        {/* <div className="flex flex-col gap-2 mb-4">
          <label htmlFor="lastName">LastName</label>
          <Field
            name="lastName"
            type="text"
            placeholder="Enter your last name..."
            className="p-4 rounded-md border border-gray-400"
          ></Field>
          <div className="text-red-500">
            <ErrorMessage name="lastName"></ErrorMessage>
          </div>
        </div> */}
        {/* <div className="flex flex-col gap-2 mb-4">
          <label htmlFor="email">Email address</label>
          <Field
            name="email"
            type="email"
            placeholder="Enter your email address..."
            className="p-4 rounded-md border border-gray-400"
          ></Field>
          <div className="text-red-500">
            <ErrorMessage name="email"></ErrorMessage>
          </div>
        </div> */}
        {/* <div className="flex flex-col gap-2 mb-4">
          <label htmlFor="intro">Introduce yourself</label>
          <Field
            name="intro"
            placeholder="Enter your introduce"
            className="p-4 rounded-md border h-[150px] 
            resize-none
          border-gray-400"
            as="textarea"
          ></Field>
          <div className="text-red-500">
            <ErrorMessage name="intro"></ErrorMessage>
          </div>
        </div> */}
        {/* <div className="flex flex-col gap-2 mb-4">
          <label htmlFor="job">Job</label>
          <Field
            name="job"
            className="p-4 rounded-md border border-gray-400"
            as="select"
          >
            <option value="frontend"> FE developer</option>
            <option value="backend"> BE developer</option>
            <option value="fullstack"> Full developer</option>
          </Field>
          <div className="text-red-500">
            <ErrorMessage name="job"></ErrorMessage>
          </div>
        </div> */}
        {/* <div className="flex items-center gap-2 mb-4">
          <Field
            name="terms"
            type="checkbox"
            className="p-4 rounded-md border border-gray-400"
          ></Field>
          <p>I accpept the terms and conditians</p>
          <div className="text-red-500">
            <ErrorMessage name="terms"></ErrorMessage>
          </div>
        </div> */}
        <div>
          <button
            type="submit"
            className="w-full p-4 bg-blue-500
        font-semibold rounded-md
        text-white"
          >
            Submit
          </button>
        </div>
      </Form>
    </Formik>
  );
};
//useField
//destructuring
//rest parameter ...
// spread opeartor
const MyInput = ({ label, ...props }) => {
  const [field, meta] = useField(props);
  console.log(props);
  //{label, ...props}
  return (
    <div className="flex flex-col gap-2 mb-4">
      <label htmlFor={props.id || props.name}>{label}</label>
      <input
        type="text"
        className="p-4 rounded-md border border-gray-400"
        {...field}
        {...props}
      />
      {meta.touched && meta.error ? (
        <div className="text-red-500">{meta.error}</div>
      ) : null}
    </div>
  );
};
const MyTextarea = ({ label, ...props }) => {
  const [field, meta] = useField(props);
  console.log(props);
  //{label, ...props}
  return (
    <div className="flex flex-col gap-2 mb-4">
      <label htmlFor={props.id || props.name}>{label}</label>
      <textarea
        type="text"
        className="p-4 rounded-md border border-gray-400 h-[150px]
        resize-none"
        {...field}
        {...props}
      />
      {meta.touched && meta.error ? (
        <div className="text-red-500">{meta.error}</div>
      ) : null}
    </div>
  );
};
const MySelectbox = ({ label, ...props }) => {
  const [field, meta] = useField(props);
  console.log(props);
  //{label, ...props}
  return (
    <div className="flex flex-col gap-2 mb-4">
      <label htmlFor={props.id || props.name}>{label}</label>
      <select
        type="text"
        className="p-4 rounded-md border border-gray-400"
        {...field}
        {...props}
      ></select>
      {meta.touched && meta.error ? (
        <div className="text-red-500">{meta.error}</div>
      ) : null}
    </div>
  );
};
const MyCheckbox = ({ children, ...props }) => {
  const [field, meta] = useField(props);
  console.log(props);
  //{label, ...props}
  return (
    <div className="flex flex-col gap-2 mb-4">
      <label className="flex item-center gap-2">
        <input className="w-5 h-5" type="checkbox" {...field} {...props} />
        {children}
      </label>
      {meta.touched && meta.error ? (
        <div className="text-red-500">{meta.error}</div>
      ) : null}
    </div>
  );
};
export default SignUpFormFinal;
