import React, { createContext } from "react";

const CountContext = createContext();

const CountProvider = (props) => {
  return <CountContext.Provider {...props}></CountContext.Provider>;
};

const CountDisplay = () => {
  const count = 0;
  return <div>The count is: {count}</div>;
};
const Counter = () => {
  const setCount = () => {};
  const increment = () => setCount((c) => c + 1);
  return (
    <button
      onClick={increment}
      className="p-4 bg-purple-400
     text-white
      rounded-md"
    >
      Increment count
    </button>
  );
};
const CountContextApp = () => {
  return (
    <div className="flex items-center justify-center p-5 gap-x-5">
      <CountProvider>
        <CountDisplay></CountDisplay>
        <Counter></Counter>
      </CountProvider>
    </div>
  );
};

export default CountContextApp;
