import React from "react";
const CountDisplay = () => {
  const count = 0;
  return <div>The count is: {count}</div>;
};
const Counter = () => {
  const setCount = () => {};
  const increment = () => setCount((c) => c + 1);
  return (
    <button onClick={increment} className="p-4 bg-purple-400 rounded-md">
      {" "}
      Increment count
    </button>
  );
};
const CountContext = () => {
  return (
    <div className="flex items-center jus">
      <CountDisplay></CountDisplay>
      <Counter></Counter>
    </div>
  );
};

export default CountContext;
