import React, { useRef, useState } from "react";

const Stopwatch = () => {
  const timerRef = useRef(null);
  const [count, setCount] = useState(0);
  const HandleStart = () => {};
  const HandleStop = () => {};
  return (
    <div>
      <h3>Time: {count} s</h3>
      <div onClick={HandleStart}>Start</div> <br />
      <div onClick={HandleStop}>Stop</div>
    </div>
  );
};

export default Stopwatch;
