import { values } from "lodash";
import React from "react";
import { useForm } from "react-hook-form";
//Bài 133: Cài đặt thư viện React Hook Form
//Bài 134: Sử dụng React Hook Form cơ bản
//Bài 135: Validation cơ bản
const SignUpFormHook = () => {
  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm();
  console.log(errors);
  const onSubmit = (values) => {
    console.log(values);
  };
  // errors = formState.errors
  return (
    <form
      onSubmit={handleSubmit(onSubmit)}
      className="p-10 w-full max-w-[500px] mx-auto"
      autoComplete="off"
    >
      <div className="flex flex-col gap-2 mb-4">
        <label htmlFor="firstName">FirstName</label>
        <input
          type="text"
          id="firstName"
          placeholder="Enter your first name..."
          className="p-4 rounded-md border border-gray-400"
          {...register("firstName", {
            required: true,
            maxLength: 10,
          })}
        />
      </div>
      <div className="flex flex-col gap-2 mb-4">
        <label htmlFor="lastName">LastName</label>
        <input
          type="text"
          id="lastName"
          placeholder="Enter your last name..."
          className="p-4 rounded-md border border-gray-400"
          {...register("lastName", {
            required: true,
            maxLength: 10,
          })}
        />
      </div>
      <div className="flex flex-col gap-2 mb-4">
        <label htmlFor="email ">Email address</label>
        <input
          type="email"
          id="email "
          placeholder="Enter your email address"
          className="p-4 rounded-md border border-gray-400"
          {...register("email")}
        />
      </div>
      <div>
        <button
          type="submit"
          className="w-full p-4 bg-blue-500
        font-semibold rounded-md
        text-white"
        >
          Submit
        </button>
      </div>
    </form>
  );
};

export default SignUpFormHook;
// sử dụng handleBlur và touched để đánh dấu input nào tương tác input nào chưa tương tác
// => để hiển thị validate
