import React, { useEffect, useState } from "react";
import axios from "axios";
// const getRandomPhotos = (page) => {
const getRandomPhotos = (page) => {
  return axios
    .get(`https://picsum.photos/v2/list?page=${page}&limit=4`)
    .then(function (res) {
      // handle success
      console.log(res.data);
      return res.data;
    })
    .catch(function (error) {
      // handle error
      console.log(error);
    });
};
// };
const Photos = () => {
  const [randomPhotos, setRandomPhotos] = useState([]);
  const [nextPage, setNextpage] = useState(1);

  const handleLoadMore = () => {
    getRandomPhotos(nextPage).then((images) => {
      const newPhotos = [...randomPhotos, ...images];
      setRandomPhotos(newPhotos);
      setNextpage(nextPage + 1);
    });
  };
  useEffect(() => {
    handleLoadMore();
    // getRandomPhotos().then((images) => {
    //   setRandomPhotos(images);
    // });
  }, []);
  return (
    <div>
      <div className="grid grid-cols-4 grid-rows-4 gap-5 p-5">
        {randomPhotos.map((item) => (
          <div key={item.id} className="p-3 bg-white shadow-md rounded-lg">
            <img
              src={item.download_url}
              alt={item.author}
              className="w-full h-full object-cover rounded-lg"
            />
          </div>
        ))}
      </div>
      <div className="text-center">
        <button
          onClick={handleLoadMore}
          className="inline-block rounded-lg px-8 py-4 bg-purple-600 text-white "
        >
          Loadmore
        </button>
      </div>
    </div>
  );
};

export default Photos;
