import React from "react";
import { useGallery } from "../../context/gallery-context";
//Bài 195: Thực hành GalleryContext - Styling
import PropTypes from "prop-types";
import { useAuth } from "../../context/auth-context";
const PhotosList = () => {
  const { photos } = useGallery();
  console.log("PhotosList", photos);
  return (
    <div className="py-10 px-5">
      <div className="grid grid-cols-4 gap-6">
        {photos.length > 0 &&
          photos.map((item) => {
            return <PhotoItem key={item.id} info={item}></PhotoItem>;
          })}
      </div>
    </div>
  );
};
const PhotoItem = ({ info: { url, isLike, id } }) => {
  const { toggleIsLike } = useGallery();
  console.log("PhotoItem", isLike);
  return (
    <div className="relative h-[300px]  cursor-pointer group">
      <img src={url} alt="" className="w-full h-full object-cover" />
      <span
        className="absolute top-2 right-2 opacity-0
       group-hover:opacity-100 transition ease-in-out
       cursor-pointer "
        onClick={() => toggleIsLike(!id)}
      >
        <svg
          width="30"
          height="25"
          viewBox="0 0 42 38"
          fill="none"
          xmlns="http://www.w3.org/2000/svg"
        >
          <path
            d="M0.166626 11.5C0.166108 8.47984 1.37993 5.58633 3.53499 3.47045C5.69005 1.35458 8.60534 0.19405 11.625 0.249979C15.2027 0.230979 18.6166 1.74826 21 4.41665C23.3833 1.74826 26.7972 0.230979 30.375 0.249979C33.3946 0.19405 36.3099 1.35458 38.4649 3.47045C40.62 5.58633 41.8338 8.47984 41.8333 11.5C41.8333 22.6583 28.5437 31.0833 21 37.75C13.4729 31.0271 0.166626 22.6666 0.166626 11.5Z"
            fill={` ${isLike ? "#FC2872" : "white"}`}
          />
        </svg>

        {/* <svg
          width="30"
          height="25"
          viewBox="0 0 42 38"
          fill="none"
          xmlns="http://www.w3.org/2000/svg"
        >
          <path
            d="M0.166626 11.5C0.166108 8.47984 1.37993 5.58633 3.53499 3.47045C5.69005 1.35458 8.60534 0.19405 11.625 0.249979C15.2027 0.230979 18.6166 1.74826 21 4.41665C23.3833 1.74826 26.7972 0.230979 30.375 0.249979C33.3946 0.19405 36.3099 1.35458 38.4649 3.47045C40.62 5.58633 41.8338 8.47984 41.8333 11.5C41.8333 22.6583 28.5437 31.0833 21 37.75C13.4729 31.0271 0.166626 22.6666 0.166626 11.5Z"
            fill="#FC2872"
          />
        </svg> */}
      </span>
      <button
        className="py-2 px-3 absolute bottom-5 left-2/4 
      text-sm rounded-md font-medium opacity-0
      group-hover:opacity-100 transition ease-in-out
      -translate-x-2/4 bg-white"
      >
        Add to cart
      </button>
    </div>
  );
};

PhotoItem.propTypes = {
  info: PropTypes.object,
  url: PropTypes.string,
  id: PropTypes.number,
  isLike: PropTypes.bool,
};
export default PhotosList;
