import { createGlobalStyle } from "styled-components";
import { GlobalClasses } from "./GlobalClasses";
export const GlobalStyle = createGlobalStyle`
  @tailwind base;
  @tailwind components;
  @tailwind utilities;
    ${GlobalClasses}
`;
