import React from "react";
import { useFormik } from "formik";
//Bài 122: Setup giao diện form cơ bản
//Bài 123: Cài đặt và sử dụng formik vào form
const SignUpForm = () => {
  const formik = useFormik({
    initialValues: {
      firtName: "",
    },
    onSubmit: (values) => {
      console.log(values);
    },
  });
  console.log("object ~ formik", formik);
  return (
    <form
      onSubmit={formik.handleSubmit}
      className="p-10 w-full max-w-[500px] mx-auto"
    >
      <div className="flex flex-col gap-2 mb-4">
        <label htmlFor="firstName">FirstName</label>
        <input
          type="text"
          id="firstName"
          name="firstName"
          placeholder="Enter your first name..."
          onChange={formik.handleChange}
          className="p-4 rounded-md border border-gray-400"
          value={formik.values.firtName}
        />
      </div>
      <div>
        <button
          className="w-full p-4 bg-blue-500
        font-semibold rounded-md
        text-white"
        >
          Submit
        </button>
      </div>
    </form>
  );
};

export default SignUpForm;
