import React, { useEffect } from "react";
import { createPortal } from "react-dom";
//Bài 180: Tối ưu code Portal nâng cao phần 1
const createPortalWrapper = () => {
  const element = document.createElement("div");
  element.id = "portal-wrapper";
  return element;
};
const portalWrapperElm = createPortalWrapper();

const Portal = () => {
  useEffect(() => {
    document.body.appendChild(portalWrapperElm);
  }, []);
  const renderContent = (
    <div className="fixed inset-0" style={{ zIndex: 888 }}>
      Lorem ipsum dolor sit amet consectetur adipisicing elit. Dolores
      blanditiis, ducimus numquam at magnam culpa nostrum fugiat rerum. Illum
      doloremque consequatur quo a repudiandae officiis repellendus.
      Exercitationem optio debitis eos!
    </div>
  );
  return createPortal(renderContent, portalWrapperElm);
  //   return <div></div>;
};

export default Portal;
