import React from "react";
import { useForm } from "react-hook-form";
import { yupResolver } from "@hookform/resolvers/yup";
import * as yup from "yup";
//Bài 133: Cài đặt thư viện React Hook Form
//Bài 134: Sử dụng React Hook Form cơ bản
//Bài 135: Validation cơ bản
//Bài 136: Sử dụng Yup cơ bản
const schema = yup.object({
  firstName: yup.string().required().maxLength(10),
});
const SignUpFormHook = () => {
  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm({
    resolver: yupResolver(schema),
  });

  console.log("SignUpFormHook ~ errors", errors);
  const onSubmit = (values) => {
    console.log(values);
  };
  // errors = formState.errors
  return (
    <form
      onSubmit={handleSubmit(onSubmit)}
      className="p-10 w-full max-w-[500px] mx-auto"
      autoComplete="off"
    >
      <div className="flex flex-col gap-2 mb-4">
        <label htmlFor="firstName">FirstName</label>
        <input
          type="text"
          id="firstName"
          placeholder="Enter your first name..."
          className="p-4 rounded-md border border-gray-400"
          {...register("firstName", {
            required: true,
            maxLength: 10,
          })}
        />
        {/* Kiểm tra nếu có errors.firstName và
        errors.firstName.type === 'required' thì in ra dòng thông báo  */}
        {errors.firstName?.type === "required" && (
          <div className="text-red-500">Please fill out this field</div>
        )}
        {errors.firstName?.type === "maxLength" && (
          <div className="text-red-500">Must be 10 charactẻ or less</div>
        )}
      </div>
      <div className="flex flex-col gap-2 mb-4">
        <label htmlFor="lastName">LastName</label>
        <input
          type="text"
          id="lastName"
          placeholder="Enter your last name..."
          className="p-4 rounded-md border border-gray-400"
          {...register("lastName", {
            required: true,
            maxLength: 10,
          })}
        />
        {errors.lastName?.type === "required" && (
          <div className="text-red-500">required</div>
        )}
      </div>
      <div className="flex flex-col gap-2 mb-4">
        <label htmlFor="email ">Email address</label>
        <input
          type="email"
          id="email "
          placeholder="Enter your email address"
          className="p-4 rounded-md border border-gray-400"
          {...register("email")}
        />
      </div>
      <div>
        <button
          type="submit"
          className="w-full p-4 bg-blue-500
        font-semibold rounded-md
        text-white"
        >
          Submit
        </button>
      </div>
    </form>
  );
};

export default SignUpFormHook;
// sử dụng handleBlur và touched để đánh dấu input nào tương tác input nào chưa tương tác
// => để hiển thị validate
