import React from "react";
import { Link, NavLink } from "react-router-dom";

const ListLink = [
  {
    id: 1,
    to: "/",
  },
  {
    id: 2,
    to: "/blog",
  },
  {
    id: 3,
    to: "/profile",
  },
];

const Nav = () => {
  return (
    <div
      className="p-5 bg-white flex shadow-lg
  items-center justify-center gap-x-5"
    >
      <NavLink
        to={"/"}
        className={({ isActive }) => (isActive ? "text-blue-300" : "")}
      >
        Home
      </NavLink>
      <NavLink
        to={"/blog"}
        className={({ isActive }) => (isActive ? "text-blue-300" : "")}
      >
        Blog
      </NavLink>
      <NavLink
        to={"/profile"}
        className={({ isActive }) => (isActive ? "text-blue-300" : "")}
      >
        Profile
      </NavLink>
    </div>
  );
};

export default Nav;
