  const { user } = useAuth();
  return (
    <div className="p-4 bg-white shadow-md flex items-center justify-center">
      <div className="flex items center gap-x-3">
        <img
          src="https://images.unsplash.com/photo-1652489997196-90c654c0b584?crop=entropy&cs=tinysrgb&fm=jpg&ixlib=rb-1.2.1&q=80&raw_url=true&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=1025"
          alt=""
          className="w-10 h-10 rounded-full object-cover"
        />
        <span className="text-sm font-medium flex items-center">
          Welcome back! <strong> truongGa</strong>
        </span>
      </div>

      <span className="text-sm font-medium"> Welcome</span>
    </div>
  );
};

export default HeaderMain;
