import React from "react";
import { useFormik } from "formik";
//Bài 122: Setup giao diện form cơ bản
//Bài 123: Cài đặt và sử dụng formik vào form
//Bài 124: Validation cơ bản với formik
const validate = (values) => {
  const errors = {};
  if (!values.firstName) {
    errors.firstName = "Required";
  } else if (values.firstName.length > 20) {
    errors.firstName = " must be 20 characters or less";
  }
  return errors;
};
const SignUpForm = () => {
  const formik = useFormik({
    initialValues: {
      firstName: "",
    },
    validate,
    onSubmit: (values) => {
      console.log(values);
    },
  });
  console.log("object ~ formik", formik);
  return (
    <form
      onSubmit={formik.handleSubmit}
      className="p-10 w-full max-w-[500px] mx-auto"
      autoComplete="off"
    >
      <div className="flex flex-col gap-2 mb-4">
        <label htmlFor="firstName">FirstName</label>
        <input
          type="text"
          id="firstName"
          name="firstName"
          placeholder="Enter your first name..."
          className="p-4 rounded-md border border-gray-400"
          value={formik.values.firstName}
          onChange={formik.handleChange}
        />
        {formik.errors.firstName ? (
          <div
            className="text-sm
        text-red-500"
          >
            {formik.errors.firstName}
          </div>
        ) : null}
      </div>
      <div>
        <button
          className="w-full p-4 bg-blue-500
        font-semibold rounded-md
        text-white"
        >
          Submit
        </button>
      </div>
    </form>
  );
};

export default SignUpForm;
