import React, { useState } from "react";
import { CSSTransition } from "react-transition-group";
import Portal from "../Portal";
//Bài 185: Tối ưu component Tooltips
const TooltipAdvanced = ({ title, children }) => {
  const [visible, setVisible] = useState(false);
  const [coords, setCoords] = useState({
    top: 0,
    left: 0,
    height: 0,
    width: 0,
  });
  const handleMouseEnter = (e) => {
    setCoords(e.target.getBoundingClientRect());
    setVisible(true);
  };
  const handleMouseLeave = () => {
    setVisible(false);
  };
  return (
    <div className="relative inline-block">
      <CSSTransition in={visible} unmountOnExit classNames="fade" timeout={300}>
        {(status) => (
          <Portal overlay={false} visible={status !== "exited"}>
            <p
              className="p-3 bg-black text-white rounded-lg 
       inline-block absolute -translate-y-full max-w-[200px]
       -translate-x-2/4 tooltip
       "
              style={{
                top: coords.top - coords.height / 4 + window.screenY,
                left: coords.left + coords.width / 2,
                transition: "all 2s",
              }}
            >
              {children}
            </p>
          </Portal>
        )}
      </CSSTransition>
      <div onMouseEnter={handleMouseEnter} onMouseLeave={handleMouseLeave}>
        {title}
      </div>
    </div>
  );
};

export default TooltipAdvanced;
