import React, { useState } from "react";
import Portal from "../Portal";

const ModalBase = ({ visible, onClose, children }) => {
  return (
    <>
      <Portal visible={visible} onClose={onClose}>
        {children}
      </Portal>
    </>
  );
};
const ComponentModalBase = () => {
  const [openModalBase, setOpenModalBase] = useState(false);
  return (
    <div>
      <button
        className="p-5 rounded-lg text-white text-center 
      bg-blue-400"
      >
        Open modal base
      </button>
      <ModalBase
        visible={openModalBase}
        onClose={() => {
          setOpenModalBase(false);
        }}
      >
        Lorem ipsum dolor sit amet consectetur adipisicing elit. Mollitia soluta
        dignissimos corporis aperiam distinctio laudantium labore! Laudantium
        quo obcaecati molestiae alias, quia ullam! Veniam eius, saepe aspernatur
        alias impedit odio.
      </ModalBase>
    </div>
  );
};
export default ComponentModalBase;
