import React, { useState } from "react";
import TooltipAdvanced from "../tooltip/TooltipAdvanced";
import ModalAdvanced from "./ModalAdvanced";
import ModalBase from "./ModalBase";
//Bài 183: Tối ưu code Portal nâng cao phần 4
//Bài 184: Tối ưu code Portal nâng cao phần 5
const AppBackUpModal = () => {
  const [openModalBase, setOpenModalBase] = useState(false);
  const [openModal, setOpenModal] = useState(false);
  return (
    <div className="p-5 flex justify-center items-center">
      <button
        className="p-5 rounded-lg text-white text-center
  bg-blue-400"
        onClick={() => {
          setOpenModalBase(true);
        }}
      >
        Open modal base
      </button>
      <button
        className="p-5 rounded-lg text-white text-center
  bg-blue-400 ml-5"
        onClick={() => {
          setOpenModal(true);
        }}
      >
        Open modal
      </button>
      <ModalBase
        visible={openModalBase}
        onClose={() => {
          setOpenModalBase(false);
        }}
      >
        <div className="bg-white p-10 w-full max-w-[320px] mx-auto rounded-lg">
          Lorem ipsum dolor sit amet consectetur adipisicing elit. Mollitia
          soluta dignissimos corporis aperiam distinctio laudantium labore!
          Laudantium quo obcaecati molestiae alias, quia ullam! Veniam eius,
          saepe aspernatur alias impedit odio.
        </div>
      </ModalBase>
      <ModalAdvanced
        visible={openModal}
        onClose={() => {
          setOpenModal(false);
        }}
        heading="WelCome Back"
        bodyClassName="w-full max-w-[320px] bg-white p-10 rounded-lg"
      >
        <div>
          <div className="flex flex-col gap-3 mb-5">
            <label htmlFor="email" className="text-sm cursor-pointer">
              Email address
            </label>
            <input
              type="email"
              className="w-full text-sm leading-normal 
        p-4
        bg-[#e7ecf3] rounded-lg"
              placeholder="Enter your email"
            />
          </div>
          <div className="flex flex-col gap-3 mb-5">
            <label htmlFor="password" className="text-sm cursor-pointer">
              Password
            </label>
            <input
              type="password"
              className="w-full text-sm leading-normal 
        p-4
        bg-[#e7ecf3] rounded-lg"
              placeholder="Enter your password"
            />
          </div>
          <button
            className="w-full max-w-[50%] p-2 font-semibold text-base
     text-white bg-[#316bff] rounded-lg"
          >
            Sing in
          </button>
        </div>
      </ModalAdvanced>
      <div className="inline-block ml-5">
        <TooltipAdvanced></TooltipAdvanced>
      </div>
    </div>
  );
};

export default AppBackUpModal;
