// 1. tao component Tooltip
// 2. Component nay co props la children, props text
// 3. ap dung portal de khi re chuot vao text thi hien thi tooltip
// 4 getBoundinhClientRect()
import React from "react";

const Tooltip = () => {
  return (
    <div>
      <TooltipPortal>Lorem ipsum dolor sit amet</TooltipPortal>
    </div>
  );
};
const TooltipPortal = ({ children, text }) => {
  return (
    <div className="fixed items-center justify-center ">
      <div
        className="max-w-[221px]
      font-normal text-sm leading-6 items-center
      bg-black rounded-lg p-3 text-white"
      >
        {children}
      </div>
      <h3>Hover me</h3>
    </div>
  );
};
export default Tooltip;
