import React, { useEffect, useState } from "react";
//https://picsum.photos/v2/list
//https://picsum.photos/v2/list?page=2&limit=100
import axios from "axios";
const getRandomPhotos = (page) => {
  return axios
    .get(`https://picsum.photos/v2/list?page=${page}&limit=8`)
    .then((res) => {
      console.log(res);
      return res.data;
    })
    .catch((error) => {
      console.log(error);
    });
};
const Photos = () => {
  const [randomPhotos, setRandomPhotos] = useState([]);
  const [nextPage, setNextPage] = useState(1);
  const handleLoadmore = () => {
    console.log("getRandomPhotos ~ nextpage", nextPage);
    getRandomPhotos(nextPage).then((images) => {
      console.log(images);
      setRandomPhotos(images);
      setNextPage(nextPage + 1);
    });
  };
  //useEffect(callback, [dependencies])
  //   useEffect(function callback() {
  //       //side-effects
  //   }, []);
  // console.log("outside");
  useEffect(() => {
    //side- effects
    // document.title = "welcome to useEffect";
    // console.log("inside");
    handleLoadmore();
  }, []);
  return (
    <>
      <div className="grid grid-cols-4 gap-5 p-5">
        {randomPhotos.length > 0 &&
          randomPhotos.map((item, index) => (
            <div
              key={item.id}
              className="p-3 bg-white shadow-lg h-[200px] rounded-lg"
            >
              <img
                src={item.download_url}
                alt={item.author}
                className="w-full h-full  object-cover rounded-lg"
              />
            </div>
          ))}
      </div>
      <div className="text-center">
        <button
          onClick={handleLoadmore}
          className="inline-block rounded-md text-white px-8 py-4 bg-purple-600"
        >
          Load more
        </button>
      </div>
    </>
  );
};

export default Photos;
