import { cleanup } from "@testing-library/react";
import React, { useEffect, useRef, useState } from "react";

const Dropdown = () => {
  const [showDropdown, setShowDropdown] = useState(false);
  const dropdownRef = useRef(null);
  const handleShowDropdown = () => {
    setShowDropdown(!showDropdown);
  };
  useEffect(() => {
    const handleClickOutDropdown = (e) => {
      if (dropdownRef.current && !dropdownRef.current.contains(e.target)) {
        console.log("click outside dropdown");
        setShowDropdown(false);
      } else {
        console.log("click inside dropdown");
      }
    };
    document.addEventListener("click", handleClickOutDropdown);
    return () => {
      document.removeEventListener("click", handleClickOutDropdown);
    };
  }, []);
  return (
    <div ref={dropdownRef} className="relative w-full max-w-[400px]">
      <div
        className="p-5 border border-gray-200
        rounded-lg w-full cursor-pointer
      "
        onClick={handleShowDropdown}
      >
        selected
      </div>
      {showDropdown && (
        <div
          className="border border-gray-200 
      absolute top-full left-0 w-full bg-white
      rounded-lg"
        >
          <div className="p-5 cursor-pointer">Javascript</div>
          <div className="p-5 cursor-pointer">vuejs</div>
          <div className="p-5 cursor-pointer">react</div>
        </div>
      )}
    </div>
  );
};

export default Dropdown;
