import React from "react";
import { useGallery } from "../../context/gallery-context";

const CartList = () => {
  const { cartItems, removeFromCart } = useGallery();
  return (
    <div className="py-10 px-5 bg-zinc-200 shadow-lg shadow-orange-400 w-ful max-w-[220px] rounded-md mx-auto">
      {cartItems.length > 0 &&
        cartItems.map((item) => {
          return (
            <div
              className="flex gap-x-5 justify-between items-center mt-2"
              key={item.id}
            >
              <img
                src={item.url}
                alt=""
                className="w-10 h-10 rounded-full object-cover"
              />
              <button
                className="rounded-lg bg-slate-500 p-2 
              text-sm text-white"
                onClick={() => removeFromCart(item.id)}
              >
                Delete
              </button>
            </div>
          );
        })}
    </div>
  );
};

export default CartList;
