import React from "react";

const SidebarMenu = (props) => {
  return (
    <div
      ref={props.ref}
      className={` w-[300px] h-full bg-gray-300 
    fixed top-0 left-0 bottom-0 z-10 shadow-md 
    transition-all
     ${props.show ? "" : "-translate-x-full"}`}
    ></div>
  );
};

export default SidebarMenu;
