import React, { useEffect } from "react";
import { createPortal } from "react-dom";
//Bài 180: Tối ưu code Portal nâng cao phần 1
//Bài 181: Tối ưu code Portal nâng cao phần 2
import PropTypes from "prop-types";
const createPortalWrapper = () => {
  const element = document.createElement("div");
  element.id = "portal-wrapper";
  return element;
};
const portalWrapperElm = createPortalWrapper();

const Portal = ({
  containerClassName = "",
  bodyClassName = "",
  containerStyle = {},
  bodyStyle = {},
  onClose = () => {},
  visible = false,
  children,
}) => {
  useEffect(() => {
    document.body.appendChild(portalWrapperElm);
  }, []);
  const renderContent = (
    <div
      className={`${containerClassName} z-[999] fixed inset-0`}
      style={containerStyle}
    >
      <div
        className="overplay absolute inset-0 bg-black 
      bg-opacity-20"
        onClick={onClose}
      ></div>
      <div
        className={` ${bodyClassName} ${
          visible ? "" : "opacity-0 invisible"
        } content relative z-10`}
        style={bodyStyle}
      >
        {children}
      </div>
    </div>
  );
  return createPortal(renderContent, portalWrapperElm);
  //   return <div></div>;
};
Portal.propTypes = {
  containerClassName: PropTypes.string,
  bodyClassName: PropTypes.string,
  containerStyle: PropTypes.object,
  bodyStyle: PropTypes.object,
  onClose: PropTypes.func,
  visible: PropTypes.bool.isRequired,
};
export default Portal;
