import React from "react";

const TextareaAutoResize = () => {
  return (
    <div>
      <textarea
        className="w-full
       max-w-[400px] p-5 rounded-sm
       border
       border-gray-300 resize-none
       focus:border-blue-400"
        placeholder="please enter text...."
      ></textarea>
    </div>
  );
};

export default TextareaAutoResize;
